def veces_que_aparece(mensaje):
    #! Convertir todas las palabras a min√∫sculas y dividir en palabras, con el fin de que si hay palabras con mayuscula tambien se cuenten
    
    palabras_minusculas = mensaje.lower().split()

    #! Inicializar un diccionario para contar las ocurrencias
    conteo_palabras = {}

    # Contar cuantas veces sale coda animal
    resultado = ""
    for animal in palabras_minusculas:
        if animal not in conteo_palabras:
            conteo_palabras[animal] = 1
        else:
            conteo_palabras[animal] += 1

    #* Construir la cadena de resultado
    for animal, times in conteo_palabras.items():
        resultado += f"{animal}{times}"

    return resultado

